mapper.insert:
INSERT INTO jerry_category.categories (
id_category, name, parent_id, sort_order,
) VALUES (
#{id_category}, #{name}, #{parent_id}, #{sort_order},
)

mapper.update:
UPDATE jerry_category.categories

    name = #{name},
    sort_order = #{sort_order},
    id_category = #{id_category}

WHERE id_category = #{id_category}
